function preload(){img=loadImage("../img/smudge-pattern.png"),img2=loadImage("../img/smudge-pattern2.png"),img3=loadImage("../img/smudge-pattern3.png"),img4=loadImage("../img/smudge-pattern4.png")}function setup(){createCanvas(windowWidth,windowHeight),background(252,247,4)}function draw(){imageMode(CENTER),image(img,mouseX,mouseX),image(img2,mouseX,mouseX),image(img3,mouseX,mouseY),image(img4,mouseX,mouseY)}var img,img2,img3,img4;